import { format as convert_format, formatISO } from "date-fns";
import type { MessageArgsProps } from "antd";

export const PREVIEW_IMAGE_WIDTH_PIXEL = 240;
export const FALLBACK_IMAGE = "data:image/png;base64,"

export function getTime(date: Date, format: string): string {
    return convert_format(date, format);
}

export function getUploadFilename(): string {
    const formattedDate = formatISO(new Date()).replace(/[:.]/g, '_');
    return `${formattedDate}.png`
}

export function dataURItoFile(dataURI: string, fileName: string) {
    // Convert base64 to raw binary data held in a string
    const byteString = atob(dataURI.split(',')[1]);

    // Separate the MIME component
    const mimeString = dataURI.split(',')[0].split(':')[1].split(';')[0];

    // Write the bytes of the string to a typed array
    const ab = new ArrayBuffer(byteString.length);
    let ia = new Uint8Array(ab);
    for (let i = 0; i < byteString.length; i++) {
        ia[i] = byteString.charCodeAt(i);
    }

    // Create a Blob with the typed array
    const blob = new Blob([ab], { type: mimeString });

    // Create a file from the Blob
    const file = new File([blob], fileName, { type: mimeString });

    return file;
}


export function notify({ api, type, content, duration = 4 }: { api: any, type: MessageArgsProps["type"], content: MessageArgsProps["content"], duration?: number }) {
    return api.open({
        type,
        content,
        style: {
            marginTop: window.innerHeight - 200
        },
        duration
    })
}